mapper.select:
SELECT w.warehouse_id as warehouseId, w.warehouse_name as warehouseName, w.warehouse_type as warehouseType, w.warehouse_status as warehouseStatus, w.warehouse_location as warehouseLocation, w.emp_id as empId, e.emp_name as empName, w.description, w.create_date as createDate, w.update_date as updateDate FROM warehouse w LEFT JOIN employee e ON w.emp_id = e.emp_id WHERE 1=1
AND w.warehouse_type = #{warehouseType}
AND w.warehouse_status = #{warehouseStatus}
AND (w.warehouse_id LIKE '%' || #{searchKeyword} || '%' OR w.warehouse_name LIKE '%' || #{searchKeyword} || '%')
ORDER BY w.create_date DESC

SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END
FROM warehouse
WHERE warehouse_id = #{warehouseId}

mapper.delete:
DELETE FROM warehouse
WHERE warehouse_id IN

    #{id}

mapper.update:
UPDATE purchase_detail
SET purchase_detail_status = #{status},
    update_at = SYSDATE
WHERE purchase_id = #{purId}
AND material_id = #{materialId}

mapper.select:

SELECT 
    location_id as "locationId",
    item_amount as "itemAmount"
FROM warehouse_item
WHERE warehouse_id = #{warehouseId}
AND product_id = #{productId}
AND item_amount < #{maxAmount}
AND use_yn = 'Y'
ORDER BY item_amount ASC

mapper.update:
UPDATE warehouse_item
SET item_amount = item_amount + #{addAmount},
    update_date = SYSDATE
WHERE location_id = #{locationId}
AND product_id = #{productId}

UPDATE warehouse_item 
SET item_amount = item_amount + #{inCount},
    update_date = SYSDATE
WHERE warehouse_id = #{warehouseId}
AND product_id = #{productId}
AND location_id = #{locationId}

UPDATE product 
SET quantity = NVL(quantity, 0) + #{inCount}
WHERE product_id = #{productId}

